// src/pages/AddTrip.js
import React, { useState } from "react";
import sendFormSql from "../services/sendFormSql";  // Importa el objeto api



const AddTrip = () => {
  const [trip, setTrip] = useState({ destination: "", date: "" });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await sendFormSql.addTrip(trip);  // Usas sendFormSql.addTrip
      console.log("Trip added:", response);
    } catch (error) {
      console.error("Error adding trip:", error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        placeholder="Destination"
        value={trip.destination}
        onChange={(e) => setTrip({ ...trip, destination: e.target.value })}
      />
      <input
        type="date"
        value={trip.date}
        onChange={(e) => setTrip({ ...trip, date: e.target.value })}
      />
      <button type="submit">Add Trip</button>
    </form>
  );
};

export default AddTrip;
