import React, { useState } from "react";
import sendFormSql from "../services/SendFormSql";

const AddTrip = () => {
  const [trip, setTrip] = useState({
    country: "",
    date: "",
    duration: "",
    cost: "",
    kmTravelled: "",
    hotel: "",
  });

  const [errors, setErrors] = useState({});

  const validateForm = () => {
    const errors = {};
    if (!trip.country.trim()) errors.country = "Country is required.";
    if (!trip.date) errors.date = "Date is required.";
    if (!trip.duration || isNaN(trip.duration) || trip.duration <= 0)
      errors.duration = "Duration must be a positive number.";
    if (!trip.cost || isNaN(trip.cost) || trip.cost <= 0)
      errors.cost = "Cost must be a positive number.";
    if (!trip.kmTravelled || isNaN(trip.kmTravelled) || trip.kmTravelled <= 0)
      errors.kmTravelled = "Kilometers travelled must be a positive number.";
    if (!trip.hotel.trim()) errors.hotel = "Hotel is required.";
    return errors;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const validationErrors = validateForm();
    if (Object.keys(validationErrors).length > 0) {
      setErrors(validationErrors);
      return;
    }
    try {
      const response = await sendFormSql.addTrip(trip);
      console.log("Trip added:", response);
      setTrip({ country: "", date: "", duration: "", cost: "", kmTravelled: "", hotel: "" });
      setErrors({});
    } catch (error) {
      console.error("Error adding trip:", error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        placeholder="Country"
        value={trip.country}
        onChange={(e) => setTrip({ ...trip, country: e.target.value })}
      />
      {errors.country && <p style={{ color: "red" }}>{errors.country}</p>}

      <input
        type="date"
        value={trip.date}
        onChange={(e) => setTrip({ ...trip, date: e.target.value })}
      />
      {errors.date && <p style={{ color: "red" }}>{errors.date}</p>}

      <input
        type="number"
        placeholder="Duration (days)"
        value={trip.duration}
        onChange={(e) => setTrip({ ...trip, duration: e.target.value })}
      />
      {errors.duration && <p style={{ color: "red" }}>{errors.duration}</p>}

      <input
        type="number"
        placeholder="Cost (USD)"
        value={trip.cost}
        onChange={(e) => setTrip({ ...trip, cost: e.target.value })}
      />
      {errors.cost && <p style={{ color: "red" }}>{errors.cost}</p>}

      <input
        type="number"
        placeholder="Kilometers Travelled"
        value={trip.kmTravelled}
        onChange={(e) => setTrip({ ...trip, kmTravelled: e.target.value })}
      />
      {errors.kmTravelled && <p style={{ color: "red" }}>{errors.kmTravelled}</p>}

      <input
        type="text"
        placeholder="Hotel"
        value={trip.hotel}
        onChange={(e) => setTrip({ ...trip, hotel: e.target.value })}
      />
      {errors.hotel && <p style={{ color: "red" }}>{errors.hotel}</p>}

      <button type="submit">Add Trip</button>
    </form>
  );
};

export default AddTrip;
